class Rhino:
    def __init__(self, am_i_a_boy):
        self.am_i_a_boy = am_i_a_boy

    # makes noise
    def make_noise(self):
        if self.am_i_a_boy:
            print("")
        else:
            print("brbr")
    # eats
    def eat(self):
        if self.am_i_a_boy:
            print("om nom, me love grass")
        else:
            print("nom")

    # poops
    def poop(self):
        if self.am_i_a_boy:
            print("prft. splattttttttttttttttttttt")
        else:
            print("prft")
    # sleeps
    def sleep(self):
        if self.am_i_a_boy:
            print("Zzzzzzzzzzz")
        else:
            print("Z")




